INSERT INTO `gxd_posts` VALUES("9", "1", "2015-10-08 17:21:29", "2015-10-08 17:21:29", "<h3 style=\"text-align: left;\"><span style=\"color: #b95e05;\"><strong>Together We Can Help Sierra Leone </strong></span></h3>\r\n<span style=\"font-weight: 400;\">Sierra Leone is at the epicenter of the worst outbreak of Ebola since the disease was identified. Up to 90% of people who contract this highly contagious virus will die if they do not receive treatment. </span>\r\n\r\n<span style=\"font-weight: 400;\">Please help<span style=\"color: #000000;\"> IDEA </span>provide treatment for those who suffer from Ebola and prevent its further spread. </span>\r\n\r\n<span style=\"font-size: 16px; font-weight: 400;\">All donations to<span style=\"color: #000000;\"> IDEA</span> are tax deductible.</span>\r\n\r\n<span style=\"color: #b95e05;\"><strong>Thank you for your generosity and for keeping the people of Sierra Leone in your heart!</strong></span>\r\n\r\n[seamless-donations]", "Donate", "", "publish", "closed", "closed", "", "donate", "", "", "2015-11-18 17:34:41", "2015-11-18 17:34:41", "", "0", "http://ideafoundation.tk/?page_id=9", "0", "page", "", "0");
