INSERT INTO `gxd_posts` VALUES("121", "5", "2015-11-07 05:02:47", "2015-11-07 05:02:47", "<h3 style=\"text-align: left;\"><span style=\"color: #b95e05;\"><strong>TOGETHER WE CAN HELP SIERRA LEONE</strong></span></h3>\n<h3 style=\"text-align: left;\"><span style=\"color: #000000;\">DONATE TO IDEA</span></h3>\n<span style=\"font-weight: 400;\">Sierra Leone is at the epicenter of the worst outbreak of Ebola since the disease was identified. Up to 90% of people who contract this highly contagious virus will die if they do not receive treatment. </span>\n\n<span style=\"font-weight: 400;\">Please help<span style=\"color: #000000;\"> IDEA </span>provide treatment for those who suffer from Ebola and prevent its further spread. </span>\n\n<span style=\"font-size: 16px; font-weight: 400;\">All donations to<span style=\"color: #000000;\"> IDEA</span> are tax deductible to the fullest extent allowed by law.</span>\n\n<span style=\"color: #b95e05;\"><strong>Thank you for your generosity and for keeping the people of Sierra Leone in your heart!</strong></span>\n\n[seamless-donations]", "Donate", "", "inherit", "closed", "closed", "", "9-autosave-v1", "", "", "2015-11-07 05:02:47", "2015-11-07 05:02:47", "", "9", "http://ideafoundation.tk/index.php/2015/10/27/9-autosave-v1/", "0", "revision", "", "0");
