INSERT INTO `gxd_posts` VALUES("196", "1", "2015-11-02 21:06:49", "2015-11-02 21:06:49", "&nbsp;\n\n&nbsp;\n\n<hr />\n\n&nbsp;\n\n&nbsp;\n\n<hr />\n\n&nbsp;\n\n<hr />\n\n&nbsp;\n\n<strong>Suspected Ebola Relapse Could be the First</strong>\n\n<span style=\"font-weight: 400;\">Pauline Cafferkey who contracted the Ebola virus from Sierra Leone has been exhibiting symptoms that could be indicators of a relapse. Cafferkey is a U.K. patient that worked in Sierra Leone but had recovered from the disease and was released from a hospital.</span>\n\n<a href=\"http://www.ischoolguide.com/articles/30710/20151024/ebola-scare-complications-uk-crozier.htm\"><span style=\"font-weight: 400;\">Read More</span></a>", "Ebola News", "", "inherit", "closed", "closed", "", "111-autosave-v1", "", "", "2015-11-02 21:06:49", "2015-11-02 21:06:49", "", "111", "http://ideafoundation.tk/111-autosave-v1/", "0", "revision", "", "0");
INSERT INTO `gxd_posts` VALUES("197", "1", "2015-11-02 21:04:45", "2015-11-02 21:04:45", "The IDEA Foundation is grateful to Sierra Leone\'s Refugee All Stars who put on an amazing show and had the audience dancing in the aisles of the Lakewood Theater in Dalllas. The Hope &amp; Health Benefit Concert &amp; Auction on November 29, 2014 raised over $16,000 for Ebola relief in Sierra Leone. All funds raised support IDEA\'s Ebola relief programs:<a href=\"http://www.theideafoundation.org/programs/medical-equipment-and-supplies/\">medical equipment and supplies</a>; and <a href=\"http://www.theideafoundation.org/programs/food-for-quarantined-villages/\">food for quarantined villages</a>.\r\n\r\nThe IDEA Foundation thanks MediSend International, the Northern Texas-Northern Louisiana Synod of the Evangelical Lutheran Church in America, the Krio Descendants Union and everyone who came.", "Sierra Leone\'s Refugee All Stars Raise $16,000 for Ebola Relief", "", "publish", "open", "open", "", "sierra-leones-refugee-all-stars-raise-16000-for-ebola-relief", "", "", "2015-11-02 21:04:45", "2015-11-02 21:04:45", "", "0", "http://ideafoundation.tk/?p=197", "0", "post", "", "0");
