INSERT INTO `gxd_posts` VALUES("199", "1", "2015-11-02 21:05:27", "2015-11-02 21:05:27", "For the past 17 years Sierra Leone\'s Refugee All Stars\' celebratory music has contradicted the horrors they witnessed during their country\'s civil war. The guitar-driven group came together in Guinean refugee camps in the late 1990s and has gone on to perform on  international stages and collaborate with rock stars.\r\n\r\nNow its members face a different plight. Since the spring, the Refugee All Stars have been living in American exile because of the Ebola epidemic.\r\n\r\n<b><a href=\"http://www.npr.org/blogs/goatsandsoda/2014/11/06/362054623/sierra-leones-refugee-all-stars-are-refugees-again-from-ebola\"target=\"_blank\">Read more about Sierra Leone\'s Refugee All Stars featured on NPR</a></b>", "Sierra Leone\'s Refugee All Stars featured on NPR", "", "publish", "open", "open", "", "sierra-leones-refugee-all-stars-featured-on-npr", "", "", "2015-11-11 19:39:46", "2015-11-11 19:39:46", "", "0", "http://ideafoundation.tk/?p=199", "0", "post", "", "0");
