INSERT INTO `gxd_posts` VALUES("245", "1", "2015-11-03 20:30:42", "2015-11-03 20:30:42", "<p>Operated by the <a href=\"http://elcsl.weebly.com/\" target=\"_blank\">Evangelical Lutheran Church of Sierra Leone</a>, Calaba Town Clinic operates in the eastern part of Freetown. The low-cost clinic is available to the entire community and helps address little health concerns before they become life-threatening issues.</p>\r\n\r\n[caption id=\"attachment_246\" align=\"alignright\" width=\"140\"]<img class=\"size-full wp-image-246\" src=\"http://ideafoundation.tk/wp-content/uploads/2015/11/clinic.png\" alt=\"Calaba Town Clinic Director, Nurse Juliana M\'bayeilla, shows off a well-stocked pharmacy cabinet.\" width=\"140\" height=\"153\" /> Calaba Town Clinic Director, Nurse Juliana M\'bayeilla, shows off a well-stocked pharmacy cabinet.[/caption]\r\n\r\n<p>Services include: midwife-assisted delivery, prenatal care, infant and well-mother exams, wound care, screening for high blood pressure and diabetes, and many other vital services.</p>\r\n\r\n&nbsp;\r\n\r\n<h4>The Calaba Town Clinic:</h4>\r\n<p><ul>\r\n	<li>Promotes the health of individuals and families in the community;</li>\r\n	<li>Provides continuity of care for individuals at high risk; and</li>\r\n	<li>Frees hospital emergency rooms to handle more dire cases.</li>\r\n</ul>\r\n</p>", "Calaba Town Clinic", "", "publish", "closed", "closed", "", "calaba-town-clinic", "", "", "2015-11-10 08:16:01", "2015-11-10 08:16:01", "", "0", "http://ideafoundation.tk/?page_id=245", "0", "page", "", "0");
