INSERT INTO `gxd_posts` VALUES("343", "5", "2015-11-10 18:12:06", "2015-11-10 18:12:06", "<p>At the <a href=\"https://ntnl.org/\" target=\"_blank\">Evangelical Lutheran Church of Sierra Leone</a>\'s facilities in Freetown, students learn marketable trades and the skills to put them to use. Each graduate receives a start-up kit which includes the equipment and supplies to open a shop of her own. Training is offered in tailoring, tie dying and soap making along with literacy and math.</p> \r\n\r\n&nbsp;\r\n<p>This program provides participants with:\r\n<ul>\r\n	<li>Practical, marketable skills;</li>\r\n	<li>Formal literacy instruction from certified teachers;</li>\r\n	<li>The skills needed to successfully operate their own small businesses; and</li>\r\n	<li>Equipment and supplies to establish their own businesses upon graduation.</li>\r\n</ul>\r\n</p>\r\n&nbsp;", "Women\'s Literacy & Vocational Program", "", "inherit", "closed", "closed", "", "263-autosave-v1", "", "", "2015-11-10 18:12:06", "2015-11-10 18:12:06", "", "263", "http://ideafoundation.tk/263-autosave-v1/", "0", "revision", "", "0");
