INSERT INTO `gxd_posts` VALUES("361", "5", "2015-11-11 09:09:34", "2015-11-11 09:09:34", "Centers for Disease Control and Prevention is collaborating with the World Health Organization and other partners in response to the ebola epidemic.\r\n\r\nThere will be a document that summarizes the important happenings in response to the outbreak, and will be regularly updated with new information.\r\n\r\nThe virus has been limited to a small area of West Africa located in Sierra Leone and Western Guiniea.\r\n\r\n<b><a href=\"http://vaccinenewsdaily.com/stories/510644447-health-professionals-provide-weekly-update-on-ebola\">Read more about Health Professionals to provide weekly updates on Ebola</a></b>", "Health professionals to provide weekly updates on Ebola", "", "inherit", "closed", "closed", "", "201-revision-v1", "", "", "2015-11-11 09:09:34", "2015-11-11 09:09:34", "", "201", "http://ideafoundation.tk/201-revision-v1/", "0", "revision", "", "0");
