INSERT INTO `gxd_posts` VALUES("363", "5", "2015-11-11 09:11:12", "2015-11-11 09:11:12", "For the past 17 years Sierra Leone\'s Refugee All Stars\' celebratory music has contradicted the horrors they witnessed during their country\'s civil war. The guitar-driven group came together in Guinean refugee camps in the late 1990s and has gone on to perform on  international stages and collaborate with rock stars.\r\n\r\nNow its members face a different plight. Since the spring, the Refugee All Stars have been living in American exile because of the Ebola epidemic.\r\n\r\n<a href=\"http://www.npr.org/blogs/goatsandsoda/2014/11/06/362054623/sierra-leones-refugee-all-stars-are-refugees-again-from-ebola\">Read more about Sierra Leone\'s Refugee All Stars featured on NPR</a>", "Sierra Leone\'s Refugee All Stars featured on NPR", "", "inherit", "closed", "closed", "", "199-revision-v1", "", "", "2015-11-11 09:11:12", "2015-11-11 09:11:12", "", "199", "http://ideafoundation.tk/199-revision-v1/", "0", "revision", "", "0");
